class Publisher(Thread):
    def __init__(self):
        super(Publisher, self).__init__()
        self.context = zmq.Context()
        self.socket = self.context.socket(zmq.PUB)
        self.socket.bind("tcp://*:5556")

    def run(self):
        while True:
            sleep(1)
            s = ",".join("file %d" % x for x in range(randrange(1, 12)))
            self.socket.send(s)


